import React, { useState } from "react";
import './navbar.css';
import favicon from '../../assets/favicon.png';
import contact from '../../assets/contact.png';
import menu from '../../assets/menu.png';
import { Link } from "react-router-dom";
function Navbar() {
    const[showMenu,setShowMenu] = useState(false);
    return (
        <nav className="navbar" style={display=showMenu?'flex':'none'}>
            <img src={favicon} alt="logo" className="logo" />
            <div className="desktopMenu">
                <Link activeClass='active' to='intro' spy={true} smooth={true} offset={-100} duration={500} className="desktopMenuListItem">Home</Link>
                <Link activeClass='active' to='skills' spy={true} smooth={true} offset={-50} duration={500} className="desktopMenuListItem">About</Link>
                <Link activeClass='active' to='works' spy={true} smooth={true} offset={-50} duration={500}  className="desktopMenuListItem">Portfolio</Link>
                <Link activeClass='active' to='clients' spy={true} smooth={true} offset={-50} duration={500} className="desktopMenuListItem">Clients</Link>
            </div>

            <button className="desktopMenubtn" onClick={() =>
                document.getElementById('contact').scrollIntoView({ behavior: 'smooth' })
            }>
                <img src={contact} alt="contact" className="desktopMenuImg" />Contect Me
            </button>

            <img src={menu} alt="Menu" className="mobMenu" />
            <div className="navMenu">
                <Link activeClass='active' to='intro'   spy={true} smooth={true} offset={-100} duration={500} className="listItem" onClick={()=>showMenu(false)}>Home</Link>
                <Link activeClass='active' to='skills'  spy={true} smooth={true} offset={-50} duration={500}  className="listItem" onClick={()=>showMenu(false)}>About</Link>
                <Link activeClass='active' to='works'   spy={true} smooth={true} offset={-50} duration={500}  className="listItem" onClick={()=>showMenu(false)}>Portfolio</Link>
                <Link activeClass='active' to='clients' spy={true} smooth={true} offset={-50} duration={500}  className="listItem" onClick={()=>showMenu(false)}>Clients</Link>
                <Link activeClass='active' to='contact' spy={true} smooth={true} offset={-50} duration={500}  className="listItem" onClick={()=>showMenu(false)}>Contacts</Link>
            </div>
        </nav>
    )
}
export default Navbar;
