import React from "react";
import { useState } from "react";

function CreateArea(){

    const [note,setnote] = useState({
        title: "",
        conntent:""
    })
    function handlechange(event){
      const {name,value} = event.target;

      setnote(prevnote=>{
        return ({
            ...prevnote,
            [name]:value
        });
      });

    }
    return (
        <div>
            <form>
                <input
                 name="title" 
                 onChange={handlechange} 
                 type="text" 
                 placeholder="Title"
                 value={note.title}

                 />
                <input 
                name = "content"
                type="textarea" 
                placeholder="Take.. a Note"
                value={note.conntent}

                />

                <button >Add</button>
            </form>
        </div>
    )
}
export default CreateArea;
