         */}

            <div class="mb-3">
            </div>
            <div class="mb-3">
                <label for="myBox" class="form-label">Example textarea</label>
                <textarea class="form-control" id="myBox" rows="3"></textarea>
            </div>

        </>
    )
}
export default TextForm;
