    const [heading,setheading] = useState("Namste Duniyaaa");
    const [ismouseover,mouseover,mouseout] = useState(false);
    function newheading(){
        setheading(" hey! Akshay Nena");
    }
    function handlemouseover(){
        mouseover(true)

    }
    function handlemouseout(){
        mouseout(true)
    }

    return (
        <div>
            <h1>{heading}</h1>
            <input type="text" placeholder="Enter your Name" />
            <button style = {{backgroundColor:ismouseover ? "black" :"blue"}}
            onClick={newheading}
            onMouseOver={handlemouseover}
            onMouseOut={handlemouseout}
            >Submit</button> 

        </div>
    )

}


export default App;
