import React, { useEffect, useState } from "react";



function App() {


    // const [name,setname] = useState("");
    // const [heading,setheading] = useState("");
    const [fname, setfname] = useState("");
    const [lname, setlname] = useState("");
    const [mobileno, setmobileno] = useState("");







    // const [isover,setmouseover] = useState(false);

    // function Submit(){
    //     setname("Hello");
    // }
    // function handleover(){
    //     setmouseover(true);
    // }
    // function handleout(){
    //     setmouseover(false);
    // }

    function handlefchange(event) {
        setfname(event.target.value);


    }
    function handlelchange(event) {

        setlname(event.target.value);
    }
    function handleMchange(event) {

        setmobileno(event.target.value);
    }


    function clicked() {
        setfname(fname)
        setlname(lname)

        //    setlname(name);
    }


    return (
        <div>
            <h1>Hello {fname} {lname}</h1>
            <h2>{mobileno}</h2>
            <input onChange={handlefchange}  type="text" placeholder="What's Your First Name" />
            <input onChange={handlelchange}  type="text" placeholder="What's Your Last Name" />
            <input onChange={handleMchange}  type="number" placeholder="What's Your Mobile No." />
            <button onClick={clicked}>
                Submit
            </button>


        </div>

    )
}
export default App;
