    // }


    // const [isover,setmouseover] = useState(false);

    // function Submit(){
    //     setname("Hello");
    // }
    // function handleover(){
    //     setmouseover(true);
    // }
    // function handleout(){
    //     setmouseover(false);
    // }
    
    const [contact,setcontact] = useState({
        fname :"",
        lname :"",
        mobileno:"",
        email:""
    })

    function handlechange(event){
        const {name,value} = event.target;

        setcontact(prevalue =>{
            if( name  === "fname"){
                return{
                 fname:value,
                 lname:prevalue.lname,
                 mobileno:prevalue.mobileno,
                 email:prevalue.email
                };
            }
            else if( name  === "lname"){
                return{
                 fname:prevalue.fname,
                 lname:value,
                 mobileno:prevalue.mobileno,
                 email:prevalue.email
                };
            } 
            else if( name  === "email"){
                return{
                 fname:prevalue.fname,
                 lname:prevalue.lname,
                 mobileno:value,
                 email:prevalue.email
                };
            } 
            else if( name  === "fname"){
                return{
                 fname:prevalue.fname,
                 lname:prevalue.lname,
                 mobileno:prevalue.mobileno,
                 email:value
                };
            }
        })

    }
    


    function clicked() {
        setfname(fname)
        setlname(lname)

        //    setlname(name);
    }


    return (
        <div>
            <h1>Hello {contact.fname} {contact.lname}</h1>
            <h2>{contact.mobileno}</h2>
            <span><p>{contact.email}</p></span>
            <input onChange={handlechange}  type="text" placeholder="What's Your First Name" />
            <input onChange={handlechange}  type="text" placeholder="What's Your Last Name" />
            <input onChange={handlechange}  type="number" placeholder="What's Your Mobile No." />
            <input onChange={handlechange}  type="text" placeholder="What's Your Email ID" />

            <button onClick={clicked}>
                Submit
            </button>


        </div>

    )
}
export default App;
