import React, { useState } from "react";
import TextForm from "./TextForm";
import Nav from "./Nav";
import Alert from "./Alert";
import About from "./About";
import {
    BrowserRouter as Router,
    Routes,
    Route,
    Link
} from "react-router-dom";

function App2() {
    const [mode, setmode] = useState('light');
    const [btntext, setBtnText] = useState('Enable Light Mode')

    const [alert, setalert] = useState('');

    const showalert = (message, type) => {
        setalert({
            msg: message,
            type: type
        })
    }

    const togglemode = () => {
        if (mode === 'light') {
            setmode('dark');
            document.body.style.background = 'grey';
            setBtnText('Enable Light Mode')
            showalert("Dark Mode is Enabled")
            document.title = "AFSSY - Dark Mode"
            // setInterval(() => {
            //     document.title = "This is wonderful!!"
            // }, 2000);
            // setInterval(() => {
            //     document.title = "download now"
            // }, 4000);

        }
        else {
            setmode('light');
            document.body.style.background = 'white';
            setBtnText('Enable Dark Mode')
            showalert("Light Mode is Enabled")
            document.title = "AFSSY - Light Mode"


        }
    }
    const toggleblue = () => {
        if (mode === 'light') {
            setmode('dark');
            document.body.style.background = 'blue';
            setBtnText('Enable Light Mode')
            showalert("Dark Mode is Enabled")
            document.title = "AFSSY - Blue Mode"


        }
        else {
            setmode('light');
            document.body.style.background = 'white';
            setBtnText('Enable Dark Mode')
            showalert("Light Mode is Enabled")
            document.title = "AFSSY - Light Mode"


        }
    }
    const togglegreen = () => {
        if (mode === 'light') {
            setmode('dark');
            document.body.style.background = 'green';
            setBtnText('Enable Light Mode')
            showalert("Dark Mode is Enabled")
            document.title = "AFSSY - Green Mode"


        }
        else {
            setmode('light');
            document.body.style.background = 'white';
            setBtnText('Enable Dark Mode')
            showalert("Light Mode is Enabled")
            document.title = "AFSSY - Light Mode"


        }
    }
    return (
        <>

            <Router>
                <Nav mode={mode} togglemode={togglemode} toggleblue={toggleblue} togglegreen={togglegreen} setBtnText={btntext} />
                <Alert alert={alert} />
                <Routes>
                    <Route exact path="/About" element={<About />}>

                    </Route>
                    <Route exact path="/Home" element={<TextForm mode={mode} heading="Enter Your Text" />
                    }>
                    </Route>
                </Routes>
            </Router>
            {/* <About /> */}

        </>



    )
}
export default App2;
