import React from "react";
import './intro.css';
import me from '../../assets/me.jpg';
import btnImg from '../../assets/hireme.png';
import { Link } from "react-router-dom";
function Intro() {
    return (
        <section id="intro">
            <div className="introContent">
                <span className="hello">Hello,</span>
                <span className="introText">I'm <span className="introName">Akshay</span><br />Web Designer</span>
                <p className="introPara">I am a skilled and professional Full Stack Web Developer with experience in visually appealing and user friendly Websites in React js </p>
                <Link><button className="btn"><img src={btnImg} alt="hire me" />Hire Me</button></Link>
            </div>
            <img src= {me} alt="" />

        </section>
    )
}
export default Intro;
