import { click } from "@testing-library/user-event/dist/click";
import React, { useState } from "react";

function TextForm(props) {
    const [text, settext] = useState('');
    // settext("This is current text");

    const handleUPclick = () => {
        let newtext = text.toUpperCase();
        console.log(newtext);
        settext(newtext);
    }
    const handleLowclick = () => {
        let newtext = text.toLocaleLowerCase();
        console.log(newtext);
        settext(newtext);

    }
    const handleclearclick = () => {
        let cleartext = '';
        settext(cleartext);
    }
    const handleonchange = (event) => {
        console.log("onchange Clicked");
        settext(event.target.value)
    }
    const handleextraspace = () => {
        let newtxt = text.split(/[ ]+/);
        settext(newtxt.join(" "));
    }

    const handlecopy = () => {
        let newtxt = document.getElementById("mybox");
        newtxt.select();
        navigator.clipboard.writeText(newtxt.value);
    }


    return (
        <>
            {/* <div className="container-heading">
                <h1 >{props.heading}</h1>
                <div>
                    <textarea id="mybox" value={text} rows={10} cols={100} onChange={handleonchange} placeholder="Enter Your Text" />
                </div>
                <button onClick={handleUPclick}>Convert to UpperCase</button>
                <button onClick={handleLowclick}>Convert to LowerCase</button>
                <button onClick={handleclearclick}>ClearText</button>
                <button onClick={handleextraspace}>Remove Extra Space</button>
                <button onClick={handlecopy}>Copy All Text</button>
            </div>
            <div className="contain" my-4>
                <h1>You Text History</h1>
                <p>{text.split("").length } words and {text.length} characters</p>
                <p>{0.008 * text.split("").length } minutes to read</p>
                <h3>Preview</h3>
                <p>{text}</p>
            </div>
         */}

        <div>
            <div class="mb-3">
            <h1>{props.heading}</h1>
                <textarea class="form-control" id="myBox" rows="10"></textarea>
            </div>
            <button onClick={handleUPclick} className="btn btn-primary mx-2">Convert to UpperCase</button>
            <button className="btn btn-primary mx-2">Convert to LowerCase</button>
            <button className="btn btn-primary mx-2">Copy Text</button>
            <button className="btn btn-primary mx-2">Clear Text</button>
            <button className="btn btn-primary mx-2">Remove Extra Spaces</button>
        </div>

        </>
    )
}
export default TextForm;
