// import React from "react";
import ReactDOM from "react-dom";
import React, { useState } from "react";
// import Card from "./Card";
// import contact from "./contact";
// import Detail from "./Detail";
// import emojipedia from "./emojipedia";
// import Emoji from "./Emoji";
// import Form from "./Form";




function App() {

    // const [fname ,setfname] = useState("");
    // const [lname,setlname] = useState("");
    // const [email,setemail] = useState("");

    const [contact, setcontact] = useState({

        fname: "",
        lname: "",
        email: ""

    });

    function handlechange(event) {

        const {name, value} = event.target;
        setcontact(prevValue => {
            if (name === "fname") {
                return {
                    fname: value,
                    lname: prevValue.lname,
                    email: prevValue.email
                }
            }
            else if (name === "lname") {
                return {
                    fname: prevValue.fname,
                    lname: prevValue.lname,
                    email: prevValue.email
                }
            }
            else if (name === "email") {
                return {
                    fname: prevValue.fname,
                    lname: prevValue.lname,
                    email: value
                } 
            };
        });

    }


return (
    <div>
        <h1>Hello {contact.fname} {contact.lname} </h1>
        <p>{contact.email}</p>
        <input onChange={handlechange} type="text" placeholder="Enter your first name" />
        <input onChange={handlechange} type="text" placeholder="Enter your last name" />
        <input onChange={handlechange} type="text" placeholder="Enter you Email" />

        <button>Submit</button>
    </div>
)



}


export default App;
