import React, { useRef } from "react";
import './contact.css';
import walmart from '../../assets/walmart.png';
import Adobe from '../../assets/adobe.png';
import microsoft from '../../assets/microsoft.png';
import facebook from '../../assets/facebook.png';
import facebookicon from '../../assets/facebook-icon.png';
import instagramicon from '../../assets/instagram.png'
import twitter from  '../../assets/twitter.png';
import youtube from '../../assets/youtube.png';
import emailjs from '@emailjs/browser';
function Contact() {
    const form = useRef();
    const sendEmail = (e) => {
        e.preventDefault();
        emailjs.sendForm('service_rxghgkx', 'template_0vkhwgs', form.current, 'fmEaI67DxL8w48PCP')
          .then((result) => {
              console.log(result.text);
          }, (error) => {
              console.log(error.text);
          });
      };

    return (
        <section id="contactPage">
            <div id="clients">
                <h1 className="contactPageTitle">My Clients</h1>
                <span className="clientDesc">Lorem ipsum dolor sit amet, consectetur adipisicing elit. Nulla incidunt neque harum accusantium, itaque, laborum veritatis voluptatibus aliquid exercitationem a dignissimos nesciunt tenetur fugit in eveniet officiis libero velit voluptates!</span>
                <div className="clientImgs">
                    <img src={walmart} alt="Client" className="clientImg" />
                    <img src={Adobe} alt="Client" className="clientImg" />
                    <img src={microsoft} alt="Client" className="clientImg" />
                    <img src={facebook} alt="Client" className="clientImg" />
                </div>
            </div>

            <div id="contact">
                <h2 className="contactPageTitle">Contact Me</h2>
                <span className="contactDesc">Lorem ipsum dolor sit amet consectetur, adipisicing elit. Aliquid nihil distinctio laboriosam aliquam eligendi doloremque</span>
                <form  className="contactform" ref={form} onSubmit={sendEmail}>
                    <input type="text" className="name" placeholder="Your Name" name="your_name" />
                    <input type="email"className="email" placeholder="Your Email" name="your_email" />
                    <textarea name="message" className="msg"  rows="5" placeholder="Your Message" ></textarea>
                    <button type="submit" value="Send" className="submitbtn">Submit</button>
                    <div className="links">
                        <img src={facebookicon} alt="Facebook" className="link" />
                        <img src={twitter} alt="Twitter" className="link" />
                        <img src={youtube} alt="Youtube" className="link" />
                        <img src={instagramicon} alt="Instagram" className="link" />
                    </div>
                </form>
            </div>

        </section>

    )
}
export default Contact;
