    })
    function handlechange(event){
        const newnote = event.target.value;
        setnote(note);

    }
    return (
        <div>
            <form>
                <input
                 name="title" 
                 onChange={handlechange} 
                 type="text" 
                 placeholder="Title"/>
                 value={note.title}
                <input 
                name = "content"
                type="textarea" 
                placeholder="Take.. a Note"/>
                value={note.conntent}

                <button >Add</button>
            </form>
        </div>
    )
}
export default CreateArea;
