import React, { useState } from "react";
import TextForm from "./TextForm";
import Nav from "./Nav";
import Alert from "./Alert";
// import About from "./About";
// import './App.css';

function App2() {
    const [mode, setmode] = useState('light');
    const [btntext, setBtnText] = useState('Enable Light Mode')

    const [alert, setalert] = useState('');

    const showalert = (message, type) => {
        setalert({
            msg: message,
            type: type
        })
        setTimeout(()=>{
              setalert(null)
        },1500)
    }

    const togglemode = () => {
        if (mode === 'light') {
            setmode('dark');
            document.body.style.background = 'grey';
            setBtnText('Enable Light Mode')
            showalert("Dark Mode is Enabled")
            document.title = "AFSSY - Dark Mode"

        }
        else {
            setmode('light');
            document.body.style.background = 'white';
            setBtnText('Enable Dark Mode')
            showalert("Light Mode is Enabled")


        }
    }
    const toggleblue = () => {
        if (mode === 'light') {
            setmode('dark');
            document.body.style.background = 'blue';
            setBtnText('Enable Light Mode')
            showalert("Dark Mode is Enabled")

        }
        else {
            setmode('light');
            document.body.style.background = 'white';
            setBtnText('Enable Dark Mode')
            showalert("Light Mode is Enabled")


        }
    }
    const togglegreen = () => {
        if (mode === 'light') {
            setmode('dark');
            document.body.style.background = 'green';
            setBtnText('Enable Light Mode')
            showalert("Dark Mode is Enabled")

        }
        else {
            setmode('light');
            document.body.style.background = 'white';
            setBtnText('Enable Dark Mode')
            showalert("Light Mode is Enabled")


        }
    }
    return (
        <div>
            {/* <About /> */}
            <Nav mode={mode} togglemode={togglemode} toggleblue={toggleblue} togglegreen={togglegreen} setBtnText={btntext} />
            <Alert alert={alert} />
            <TextForm mode={mode} heading="Enter Your Text" />
        </div>



    )
}
export default App2;
