import React, { useState } from "react";

function TextForm(props) {
    const [text, settext] = useState('Enter Your Name');
    // settext("This is current text");

    const handleUPclick = ()=>{
         console.log("UpperCase Clicked");
         settext("You haved Clicked on handleUPClick");
    }
    const handleonchange = ()=>{
        console.log("onchange Clicked");
   }

    return (
        <div>
            <h1 value={text}>{props.heading}</h1>
            <div>
            <input type="text" onChange={handleonchange} placeholder="Enter Your Text" />
            </div>
            <button onClick={handleUPclick}  >Convert to UpperCase</button>
        </div>
    )
}
export default TextForm;
