    // const [isover,setmouseover] = useState(false);

    // function Submit(){
    //     setname("Hello");
    // }
    // function handleover(){
    //     setmouseover(true);
    // }
    // function handleout(){
    //     setmouseover(false);
    // }

    function handlechange(event){
        setname(event.target.value);
    }
    function clicked(){
        setheading(name);
    }

    
 return (
    <div>
    <h1>Hello {heading}</h1>
    <input onChange= {handlechange} type="text" placeholder="What's Your First Name" />
    <input onChange= {handlechange} type="text" placeholder="What's Your Last Name" />

    <button onClick={clicked}>
        Submit
    </button>
     
     
    </div>
   
 )
}
export default App;
